    queryFn: settingsApi.getLLM,
  });
}

export function useUpdateLLMSettings() {
  const queryClient = useQueryClient();
  return useMutation({
    mutationFn: settingsApi.updateLLM,
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['settings', 'llm'] });
    },
  });
